        MyThread mt=new MyThread();
        mt.getThread().start(); //通过返回内部类的对象来开辟第一个线程
        mt.getThread().start(); //通过返回内部类的对象来开辟第二个线程
        mt.getThread().start(); //通过返回内部类的对象来开辟第三个线程
        //由于这三个线程是通过同一个匿名对象来开辟的，所以run()里方法访问的是同一个index
    }
